import { useState } from 'react'
import reactLogo from './assets/react.svg'
import viteLogo from '/vite.svg'
import './App.css'

function App() {
  const [count1, setCount1] = useState(0);
  const [count2, setCount2] = useState(0);

  function productCard1(count1) {
    const name = "Мороженое";
    const cost = "200 рублей";
    return(
      <div>
        <img src="data:image/jpeg;base64,"/>
        <h5>Название товара: {name}</h5>
        <h5>Стоимость товара: {cost}</h5>
        <p>Добавить в корзину: {count1}</p>
        <button onClick={() => setCount1(count1 - 1)} disabled={count1 == 0}>-</button>
        <button onClick={() => setCount1(count1 + 1)}>+</button>
      </div>
    );
  }

  function productCard2(count2) {
    const name = "Арбуз";
    const cost = "400 рублей";
    return(
      <div>
        <img src="data:image/jpeg;base64,"/>
        <h5>Название товара: {name}</h5>
        <h5>Стоимость товара: {cost}</h5>
        <p>Добавить в корзину: {count2}</p>
        <button onClick={() => setCount2(count2 - 1)} disabled={count2 == 0}>-</button>
        <button onClick={() => setCount2(count2 + 1)}>+</button>
      </div>
    );
  }

  function Korzina(count1, count2) {
    return <h3>Товары в корзине: {count1} мороженных, {count2} арбузов</h3>
  }

  return(
    <div className='container'>
      <h3>{productCard1(count1)}</h3>
      <h3>{productCard2(count2)}</h3>
      <h3>{Korzina(count1, count2)}</h3>
    </div>
  );
  }

export default App


